CustomField.formula:
IF(AND(
/*Checking if the requested date is in between the start and end date for daylight saving time*/
(DATEVALUE(RequestTime) > (DATE ( YEAR (TODAY()),3,1) + ( 14 - CASE( MOD(DATE ( YEAR (TODAY()),3,1) - DATE (1900,1,7),7) ,0,7,MOD(DATE ( YEAR (TODAY()),3,1) - DATE (1900,1,7),7))))),
(DATEVALUE(RequestTime) < (DATE ( YEAR (TODAY()),11,1) + ( 7 - CASE( MOD(DATE ( YEAR (TODAY()),11,1) - DATE (1900,1,7),7) , 0,7,MOD(DATE ( YEAR (TODAY()),11,1) - DATE (1900,1,7),7)))))
),
HOUR(TIMEVALUE(RequestTime)) + ( ( TIMEVALUE(NOW()) - TIMENOW() )/(3600*1000*24)) + 1,
HOUR(TIMEVALUE(RequestTime)) + ( ( TIMEVALUE(NOW()) - TIMENOW() )/(3600*1000*24))
)
